contracts/MultisigWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import { SignatureChecker } from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract MultisigWallet {
    uint256 public requiredSignersCount;
    mapping(address => bool) public signers;
    uint256 public signersCount;

    /// @notice Signatures have already been registered
    mapping(bytes32 messageHash => mapping(address signer => bool)) public alreadyVerified;

    constructor(uint256 _requiredSigners, address[] memory _signers) {
        require(_requiredSigners > 0, "_requiredSigners must be greater than zero!");
        require(_signers.length >= _requiredSigners, "_requiredSigners > _signers.length");
        requiredSignersCount = _requiredSigners;
        for (uint256 i; i < _signers.length; ++i) {
            require(_signers[i] != address(0), "_signers contains zero address!");
            require(signers[_signers[i]] == false, "signer exists!");
            signers[_signers[i]] = true;
        }
        signersCount = _signers.length;
    }

    function submitTransaction(
        address[] calldata _signers,
        bytes[] calldata _signatures,
        address _target,
        uint256 _value,
        bytes calldata _data
    ) external payable {
        bytes32 _messageHash = ECDSA.toEthSignedMessageHash(
            keccak256(
                abi.encodePacked(
                    block.chainid,
                    address(this),
                    MultisigWallet.submitTransaction.selector,
                    _target,
                    _value,
                    _data
                )
            )
        );
        uint256 verifiedSignsCount;
        for (uint256 i; i < _signers.length; ++i) {
            require(alreadyVerified[_messageHash][_signers[i]] == false, "signature already used!");
            require(
                SignatureChecker.isValidSignatureNow(_signers[i], _messageHash, _signatures[i]),
                "signature!"
            );
            alreadyVerified[_messageHash][_signers[i]] = true;
            ++verifiedSignsCount;
        }
        require(verifiedSignsCount >= requiredSignersCount, "requiredSigners!");

        (bool success, ) = _target.call{ value: _value }(_data);
        require(success, "transaction call failure!");
    }
}

contracts/tokens/IssuedERC1155.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;

import { ERC1155Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract IssuedERC1155 is Initializable, ERC1155Upgradeable, OwnableUpgradeable {
    uint256 public originalChain;
    bytes public originalToken;
    mapping(uint256 => string) uris;

    function uri(uint256 _tokenId) public view override returns (string memory) {
        return uris[_tokenId];
    }

    function uriBatch(uint256[] memory _tokenIds) public view returns (string[] memory) {
        uint256 l = _tokenIds.length;
        string[] memory _uris = new string[](l);
        for(uint256 i; i < l; i++) {
            _uris[i] = uris[_tokenIds[i]];
        }
        return _uris;
    }

    function initialize(bytes memory _originalToken) external initializer {
        ERC1155Upgradeable.__ERC1155_init("");
        OwnableUpgradeable.__Ownable_init();
        originalChain = block.chainid;
        originalToken = _originalToken;
    }

    function getOriginalTokenInfo() external view returns (uint256, bytes memory) {
        return (originalChain, originalToken);
    }

    function mint(address _recipient, uint256 _tokenId, uint256 _amount, string calldata _uri) external onlyOwner {
        uris[_tokenId] = _uri;
        _mint(_recipient, _tokenId, _amount, "");
    }

    function mintBatch(
        address _recipient,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts,
        string[] memory _uris
    ) external onlyOwner {
        require(_tokenIds.length == _uris.length, "IssuedERC1155: _tokenIds and _uris length mismatch");
        uint256 l = _tokenIds.length;
        for(uint256 i; i < l; i++) {
            uris[_tokenIds[i]] = _uris[i];
        }
        _mintBatch(_recipient, _tokenIds, _amounts, "");
    }

    function burn(address _account, uint256 _tokenId, uint256 _amount) external onlyOwner {
        _burn(_account, _tokenId, _amount);
    }

    function burnBatch(
        address _account,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts
    ) external onlyOwner {
        _burnBatch(_account, _tokenIds, _amounts);
    }

    function permissionedTransferFrom(
        address _from,
        address _to,
        uint256 _tokenId,
        uint256 _amount
    ) external onlyOwner {
        _safeTransferFrom(_from, _to, _tokenId, _amount, "");
    }

    function permissionedBatchTransferFrom(
        address _from,
        address _to,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts
    ) external onlyOwner {
        _safeBatchTransferFrom(_from, _to, _tokenIds, _amounts, "");
    }
    
    constructor() {
        _disableInitializers();
    }
}

