pragma solidity 0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SignatureChecker } from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { MessageHashUtils } from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import { IYarConnector } from "./interfaces/IYarConnector.sol";
import { IYarApp } from "./interfaces/IYarApp.sol";

contract YarConnector is IYarConnector, ReentrancyGuard {
    using SafeERC20 for IERC20;
    address public relayer;
    address public feeOracle;
    address public feeToken;

    Transaction internal _transaction;

    function transaction() external view returns(Transaction memory) {
        return _transaction;
    }

    event CrossCall(address peer, uint256 targetChainId, address target, bytes data);

    // event SendFees(address from, address feesToken, uint256 amount);

    constructor(address intialRelayer, address initialFeeOracle, address initailFeeToken) {
        relayer = intialRelayer;
        feeOracle = initialFeeOracle;
        feeToken = initailFeeToken;
    }

    function crossCall(
        uint256 targetChainId,
        address target,
        bytes calldata data,
        uint256 feeAmount,
        uint256 signatureExpired,
        bytes calldata signature
    ) external payable {  
        require(block.timestamp < signatureExpired, "signatureExpired!");
        require(
            SignatureChecker.isValidSignatureNow(
                feeOracle,
                MessageHashUtils.toEthSignedMessageHash(
                    keccak256(
                        abi.encodePacked(
                            block.chainid,
                            targetChainId,
                            target,
                            data,
                            feeAmount,
                            signatureExpired
                        )
                    )
                ),
                signature
            ),
            "signature!"
        );
        if (feeAmount > 0) {
            if (feeToken == address(0)) {
                require(msg.value == feeAmount, "feeAmount!");
            } else {
                IERC20(feeToken).safeTransferFrom(msg.sender, address(this), feeAmount);
            }
        }

        emit CrossCall(msg.sender, targetChainId, target, data);
    }

    function onCrossCall(
        uint256 initialChainId,
        address initialPeer,
        IYarApp target,
        bytes calldata data
    ) external payable nonReentrant {
        require(msg.sender == relayer, "only relayer!");
        _transaction = Transaction(initialChainId, initialPeer);
        (bool success, ) = address(target).call(data);
        require(success, "onCrossCall failed!");
        delete _transaction;
    }
}
