pragma solidity 0.8.18;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import { ERC1967ProxyCreate2 } from "./utils/ERC1967ProxyCreate2.sol";
import { IssuedERC20 } from "./tokens/IssuedERC20.sol";
import { IAddressBook } from "./interfaces/IAddressBook.sol";

contract Treasury is UUPSUpgradeable {
    using SafeERC20 for IERC20;
    IAddressBook public addressBook;

    function initialize(address _addressBook) public initializer {
        addressBook = IAddressBook(_addressBook);
    }

    receive() external payable {}

    function withdraw(address _token, uint256 _amount, address _recipient) external {
        addressBook.requireOnlyOwner(msg.sender);
        if (_token == address(0)) {
            (bool success, ) = _recipient.call{ value: _amount }("");
            require(success, "transfer failed!");
        } else {
            IERC20(_token).safeTransfer(_recipient, _amount);
        }
    }

    function _authorizeUpgrade(address) internal view override {
        addressBook.requireOnlyOwner(msg.sender);
    }

    constructor() {
        _disableInitializers();
    }
}
","keccak256":"0x082983dabb8622e06c27dd16598d27f1d06f0c634a211eac75e317b781e05ef3","license":"UNLICENSED"},"contracts/interfaces/IAddressBook.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

interface IAddressBook {
    function requireOnlyOwner(address _account) external view;
    function requireTransferValidator(address _account) external view;
    function requireTransferApprover(bytes32 _messageHash, bytes calldata _signature) external view;
    function treasury() external view returns(address);
    function feeToken() external view returns(address);
    function chainId() external view returns(uint256);
}
","keccak256":"0x5c856365821c62b1b6e3f1f30bb440c8496603ed0e4453c73c29819937c0caa9","license":"MIT"},"contracts/tokens/IssuedERC20.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;

import { ERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract IssuedERC20 is Initializable, ERC20Upgradeable, OwnableUpgradeable {
    uint256 public originalChain;
    address originalToken;
    uint8 internal originalTokenDecimals;
    string public originalTokenName;
    string public originalTokenSymbol;

    function initialize(
        uint256 _originalChain,
        address _originalToken,
        string memory _originalTokenName,
        string memory _originalTokenSymbol,
        uint8 _originalTokenDecimals
    ) external initializer {
        ERC20Upgradeable.__ERC20_init(
            string(abi.encodePacked("y", _originalTokenName)),
            string(abi.encodePacked("y", _originalTokenSymbol))
        );
        OwnableUpgradeable.__Ownable_init();
        originalTokenName = _originalTokenName;
        originalTokenSymbol = _originalTokenSymbol;
        originalChain = _originalChain;
        originalToken = _originalToken;
        originalTokenDecimals = _originalTokenDecimals;
    }

    function getOriginalTokenInfo() external view returns (uint256, address, string memory, string memory, uint8) {
        return (originalChain, originalToken, originalTokenName, originalTokenSymbol, decimals());
    }

    function mint(address _recipient, uint256 _amount) external onlyOwner {
        _mint(_recipient, _amount);
    }

    function burn(address _from, uint256 _amount) external onlyOwner {
        _burn(_from, _amount);
    }

    function permissionedTransferFrom(
        address from,
        address to,
        uint256 amount
    ) external onlyOwner {
        _transfer(from, to, amount);
    }

    function decimals() public view virtual override returns (uint8) {
        return originalTokenDecimals;
    }

    constructor() {
        _disableInitializers();
    }
}
